package com.example.coolonaryapp.ui.home

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.lifecycle.ViewModelProvider
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.coolonaryapp.core.adapter.HeroAdapter
import com.example.coolonaryapp.core.model.Makanan
import com.example.coolonaryapp.databinding.FragmentHomeBinding

class HomeFragment : Fragment() {

    private var _binding: FragmentHomeBinding? = null

    // This property is only valid between onCreateView and
    // onDestroyView.
    private val binding get() = _binding!!

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?
    ): View {
        val homeViewModel = ViewModelProvider(this).get(HomeViewModel::class.java)

        _binding = FragmentHomeBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)


        val listMakanan = listOf(
            Makanan(
                image = "data:image/jpeg;base64,",
                exp = "12-1-2023",
            ), Makanan(
                image = "data:image/jpeg;base64,",
                exp = "12-3-2023",
            ), Makanan(
                image = "data:image/jpeg;base64,",
                exp = "12-4-2023",
            ), Makanan(
                image = "data:image/jpeg;base64,",
                exp = "12-12-2023",
            )
        )


        val adapterMakanan = HeroAdapter(listMakanan)

        binding.recyclerViewMakanan.apply {
            binding.recyclerViewMakanan.layoutManager =
                LinearLayoutManager(requireContext(), LinearLayoutManager.HORIZONTAL, false)
//            layoutManager = LinearLayoutManager(requireContext())
            adapter = adapterMakanan
        }

    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}